doEStructMacro.cutModeInit:
int cutBinMode = 3;
int numberOfAnalyses = mbNBins-1;
// choose the mode for the binning
StEStructCutBin* cb=StEStructCutBin::Instance();
cb->setMode(cutBinMode);
StEStruct2ptCorrelations** analysis = new StEStruct2ptCorrelations*[numberOfAnalyses];
StEStruct2ptCorrelations* currentAnalysis;

doEStructMacro.pairCutsWrite:
if (analysis) {
    StEStructPairCuts* pcuts = &analysis[i]->getPairCuts();
    if (pcuts) {
        pcuts->printCuts(ofs);
        pcuts->writeCutHists(tfc);
    } else {
        ofs<<" No pair cuts for this centrality."<<endl;
    }
}
