doEStructMacro.declarePtDefHistograms:
int numberOfAnalyses = 1;
cent->setPts(ptCut,mbNPts,ptMultCut,mbNPtBins);
TH1D *ptCenClass = new TH1D("ptCenClass","ptCenClass",mbNPtBins,-1.5,mbNPtBins-0.5);
TH1D *ptRanges     = new TH1D("ptRanges","ptRanges",mbNPts,-0.5,mbNPts+0.5);
for (int i=0;i<mbNPts;i++) {
    ptRanges->Fill(i,ptCut[i]);
}
TH1D *impactPtDefs = new TH1D("impactPtDefs","impactPtDefs",mbNPtBins,0.5,mbNPtBins+0.5);
for (int i=0;i<mbNPtBins;i++) {
    impactPtDefs->Fill(i,ptMultCut[i]);
}
StEStructFluctAnal** analysis = new StEStructFluctAnal*[numberOfAnalyses];
StEStructFluctAnal* currentAnalysis;

doEStructMacro.savePtDefHistograms:
analysis[0]->writeQAHists(tfq);
cenClass->Write();
ptCenClass->Write();
ptRanges->Write();
impactDefs->Write();
impactPtDefs->Write();
