doEStructMacro.main:
void doEStruct( const char* filelist,
                const char* outputDir,
                const char* scriptDir,
                int maxNumEvents = 1000 ) {

    char cutFile[1024];
    char buffer[1024];

    const char* scratchDir = 
    sprintf(cutFile,"%s/CutsFile.txt",scriptDir);

    gROOT->LoadMacro("load2ptLibs.C");
    load2ptLibs();
    gROOT->LoadMacro("getOutFileName.C");
    gROOT->LoadMacro("support.C");



    // simple (global) centrality definition ...not persistant to event file.
    StEStructCentrality* cent=StEStructCentrality::Instance();
    const double mbBins[] = 
    int mbNBins = 
    cent->setCentralities(mbBins,mbNBins);

    TH1D *cenClass   = new TH1D("cenClass","cenClass",mbNBins,-1.5,mbNBins-0.5);
    TH1D *centralityDefs = new TH1D("centralityDefs","centralityDefs",mbNBins,0.5,mbNBins+0.5);
    for (int i=0;i<mbNBins;i++) {
        centralityDefs->Fill(i,mbBins[i]);
    }



    char *analysisType = 

    const  double ptCut[] = 
    int mbNPts = 
    const  double ptMultCut[] = 
    int mbNPtBins = 







    // Need an EStruct maker.
    StEStructAnalysisMaker *estructMaker = new StEStructAnalysisMaker(analysisType);

    // Set up the Analysis codes and initialize the cuts.

//    char** dataDirs             = getDirNames("data",numberOfAnalyses);
//    char** cutDirs              = getDirNames("cuts",numberOfAnalyses);
//    char** QADirs               = getDirNames("QA",numberOfAnalyses);
    char** outputFile           = new char*[numberOfAnalyses];
    char** qaoutputFile         = new char*[numberOfAnalyses];
    StEStructEventCuts** ecuts  = new StEStructEventCuts*[numberOfAnalyses];
    StEStructTrackCuts** tcuts  = new StEStructTrackCuts*[numberOfAnalyses];
    bool* skipMake = new bool[numberOfAnalyses];
    for(int i=0;i<numberOfAnalyses;i++){
        TH1::AddDirectory(kFALSE);
        ecuts[i]      = new StEStructEventCuts(cutFile);
        tcuts[i]      = new StEStructTrackCuts(cutFile);
        TH1::AddDirectory(kTRUE);
        skipMake[i]   = true;
        if (!strcmp(analysisType,"StEStructFluctuation") && numberOfAnalyses == 1) {
            outputFile[i]   = getOutFileName(outputDir,scratchDir,"data","");
            qaoutputFile[i] = getOutFileName(outputDir,scratchDir,"QA","");
        } else {
            sprintf(buffer,"_cent%i",i);
            outputFile[i]   = getOutFileName(outputDir,scratchDir,"data",buffer);
            qaoutputFile[i] = getOutFileName(outputDir,scratchDir,"QA",buffer);
        }
    }
    skipMake[0] = false;








    //  build the numberOfAnalyses readers and numberOfAnalyses analysis objects
    //  analysis = analysis interface and contains pair-cut object (needs file)
    for(int i=0;i<numberOfAnalyses;i++){

        analysis[i]->setCutFile(cutFile,cent);
        analysis[i]->setOutputFileName(outputFile[i]);
        analysis[i]->setQAOutputFileName(qaoutputFile[i]);
        estructMaker->SetReaderAnalysisPair(readers[i],analysis[i]);
    }


    int istat=0, i=0;
    estructMaker->Init();
    estructMaker->startTimer();

    int counter=0;
    while (istat!=2) {

        istat=estructMaker->Make();

        int iCen;
        int iPtCen;

        if (currentAnalysis && currentAnalysis->mCurrentEvent) {
            iCen = currentAnalysis->mCurrentEvent->Centrality();
            cenClass->Fill(iCen);

        }



        i++; counter++;
        if (counter==100) {
            cout<<"doing event ="<<i<<endl;
            counter=0;
        }
        if ( maxNumEvents!=0 && i>=maxNumEvents ) {
            istat=2;
        }
    }
    estructMaker->stopTimer();
    char* statsFileName=getOutFileName(outputDir,scratchDir,"stats","");
    ofstream ofs(statsFileName);

    ofs<<endl;
    estructMaker->logAnalysisTime(ofs);
    estructMaker->logInputEvents(ofs);
    estructMaker->logOutputEvents(ofs);
    estructMaker->logOutputRate(ofs);

    // This loop create cuts files (one per analysis object) and adds to
    // stats file (one per job). Could split into separate loops except
    // the pairCutsWrite element mixes them.
    for (int i=0;i<numberOfAnalyses;i++) {
        ofs<<"  *************** ";
        ofs<<" Cut Stats for Analysis Number = "<<i;
        ofs<<"  *************** "<<endl;
        ecuts[i]->printCuts(ofs);
        tcuts[i]->printCuts(ofs);

        sprintf(buffer,"cuts_cent%i",i);
        if (!strcmp(analysisType,"StEStructFluctuation") && numberOfAnalyses == 1) {
            char *rootCutFile = getOutFileName(outputDir,scratchDir,"cuts","");
        } else {
            sprintf(buffer,"_cent%i",i);
            char *rootCutFile = getOutFileName(outputDir,scratchDir,"cuts",buffer);
        }
        TFile *tfc = new TFile(rootCutFile,"RECREATE");
        ecuts[i]->writeCutHists(tfc);
        tcuts[i]->writeCutHists(tfc);


        tfc->Close();
    }
    ofs.close();

    // 
    // --> root QA file
    // 
    for (int i=0;i<numberOfAnalyses;i++) {
        analysis[i]->writeQAHists();
    }

    char *centralityDefFile = getOutFileName(outputDir,scratchDir,"centralityDefs","");
    TFile *tfcent = new TFile(centralityDefFile,"RECREATE");
    cenClass->Write();
    centralityDefs->Write();


    tfcent->Close();

    estructMaker->Finish();
}
